import React, { Component } from 'react';
import './App.css'; 
// import '/roofpig_and_three.min.js'
import axios from 'axios';

class AddUser extends Component { 
  constructor(props){ 
    super(props); 
    this.state = { 
      hello: 'noValue', 
      username:"noValue", 
      password:"noValue"
    }
  }



    pushRequest = ()=>{ 
      axios.push(`/addUsers/${this.state.username}/${this.state.password}`); 
        
    } 

    setStates = (event)=>{ 
      this.setState({username: event.target.username}); 
      this.setState({password: event.target.password});
    } 

    handleChange = (event) =>{
      this.setState({hello: event.target.hello});
    }
  
    handleSubmit = (event) =>{
      alert('A name was submitted: ' + this.state.hello);
      event.preventDefault();
    }
  

