    //             </label> 
                
    //             <input type="submit" value="Submit"/> 
                
    //         </form> 
    //     </div>
    //   );
    // } 

        render() {
          return (
            <form onSubmit={this.handleSubmit}>
              <label>
                Name:
                <input type="text" value={this.state.hello} onChange={this.handleChange} />
              </label>
              <input type="submit" value="Submit" />
            </form>
          );
        }
  }
  
  export default AddUser;
