import React, { Component } from 'react';
import './App.css'; 
// import '/roofpig_and_three.min.js'
import axios from 'axios';

class AddUser extends Component { 
  constructor(){ 
    super(); 
    this.state = { 
      data: "hello", 
      username:"null", 
      password:"null"
    }
  }



    pushRequest = ()=>{ 
      axios.push(`/addUsers/${this.state.username}/${this.state.password}`); 
        
    } 

    setStates = (e)=>{ 
      this.setState({username: e.target.username}); 
      this.setState({password: e.target.password});
    } 

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('A username was submitted: ' + this.state.username); 
      alert('A password was submitted: '+ this.state.password);
      event.preventDefault();
    }

    render() {
      return (
        <div> 
            <form className="form-inline">
                <label className="required">* required </label>
                <div className="form-group">
                    <input type="text" className="form-control inputArea" onChange={this.handleChange} value={this.state.username} id="Username" placeholder="Username *"></input> 
                    <input type="text" className="form-control inputArea" onChange={this.handleChange} value={this.state.password} id="Password" placeholder="Password *"></input> 
                    {/* <input type="text" value={this.state.value} onChange={this.handleChange} /> */}
                </div>
            </form> 
            {/* <button onClick={this.pushRequest}>Click</button> 
            <p>username={this.state.data[0].userName}</p> 
            <p>password={this.state.data[0].userPassword}</p>  */}
        </div>
      
      );
    }
  }
  
  export default AddUser;
