import React, { Component } from 'react';
import './App.css'; 
// import '/roofpig_and_three.min.js'
import axios from 'axios';
import Users from './Users';

class UpdateUser extends Component { 
  constructor(props){ 
    super(props); 
    this.state = { 
    
       show:false
      
    }
  } 

    updateRequest = ()=>{  
        // axios.delete(`/deleteUser/${this.props.userID}`); 
        axios.put(`/updateUser/${this.props.username}/${this.props.password}/${this.props.userID}`)
      }  
    showForm=()=>{ 
        this.setState({show:true});
    }
    render() {   
      if(!this.state.show){ 
          return( 
          <button onClick={this.showForm}>Update User</button>
          );
      }
      else{
        return ( 
            <form onSubmit={this.handleSubmit} className="form-inline">
                <label className="required">* required </label>
                
                    <label>
                    <input type="text" name='userName' target='one' className="form-control inputArea" onChange={this.setStates} userName={this.state.userName} id="Username" placeholder="Username *"></input> 
                    <input type="text" name='password' target='two' className="form-control inputArea" onChange={this.setStates} password={this.state.password} id="Password" placeholder="Password *"></input> 
                    </label> 
                
                <input type="submit" value="Submit" onClick={this.updateRequest}/> 
            
            </form> 
                // <a>      
                
                //     <button onClick={this.updateRequest}>Update User</button>
                // </a>
        
        );   
      }
    //   }
      
    }
   
  }
  
  export default UpdateUser;
