import React, { Component } from 'react';
import './App.css'; 
// import '/roofpig_and_three.min.js'
import axios from 'axios';

class AddUser extends Component { 
  constructor(props){ 
    super(props); 
    this.state = { 
      hello: 'noValue',
      username:'', 
      password:''
    }
  }



    pushRequest = ()=>{ 
      axios.push(`/addUsers/${this.state.username}/${this.state.password}`); 
        
    } 

    setStates = (event)=>{ 
      this.setState({username: event.target.value[0]}); 
      this.setState({password: event.target.value[1]});
    } 

    handleChange = (event) =>{
      this.setState({hello: event.target.value});
    }
  
    // handleSubmit = (event) =>{
    //   alert('A name was submitted: ' + this.state.hello);
    //   event.preventDefault();
    // }
  

    handleSubmit=(event) =>{ 
      console.log('A username was submitted: ' + this.state.username); 
      console.log('A password was submitted: '+ this.state.password);
      event.preventDefault();
    }

    render() {
      return (
        <div> 
            <form onSubmit={this.handleSubmit} className="form-inline">
                <label className="required">* required </label>
                
                <label>
                  <input type="text" target='one' className="form-control inputArea" onChange={this.setStates} username={this.state.username} id="Username" placeholder="Username *"></input> 
                  <input type="text" target='two' className="form-control inputArea" onChange={this.setStates} password={this.state.password} id="Password" placeholder="Password *"></input> 
                </label> 
                
                <input type="submit" value="Submit"/> 
                
            </form> 
        </div>
      );
    } 

        // render() {
        //   return (
        //     <form onSubmit={this.handleSubmit}>
        //       <label>
        //         Name:
        //         <input type="text" onChange={this.handleChange} />
        //       </label>
        //       <input type="submit" value="Submit" />
        //     </form>
        //   );
        // }
  }
  
  export default AddUser;
