import React, { Component } from 'react';
import './App.css'; 
// import '/roofpig_and_three.min.js'
import axios from 'axios';

class AddUser extends Component { 
  constructor(props){ 
    super(props); 
    this.state = { 
       
      username:"noValue", 
      password:"noValue"
    }
  }



    pushRequest = ()=>{ 
      axios.push(`/addUsers/${this.state.username}/${this.state.password}`); 
        
    } 

    setStates = (e)=>{ 
      // this.setState({username: e.target.username}); 
      // this.setState({password: e.target.password});
    } 

  

    handleSubmit=(e) =>{ 
      console.log('A username was submitted: ' + this.state.username); 
      console.log('A password was submitted: '+ this.state.password);
      e.preventDefault();
    }

    render() {
      return (
        <div> 
            <form onSubmit={this.handleSubmit} className="form-inline">
                <label className="required">* required </label>
                
                <label>
                  <input type="text" className="form-control inputArea" onChange={this.setStates} username={this.state.username} id="Username" placeholder="Username *"></input> 
                  <input type="text" className="form-control inputArea" onChange={this.setStates} password={this.state.password} id="Password" placeholder="Password *"></input> 
                </label> 
                
                <input type="submit" value="Submit"/> 
                
            </form> 
        </div>
      );
    }
  }
  
  export default AddUser;
